mapper.select:
select goods_name goodsName,id goodsId,banner_url bannerUrl,price,sku_store skuStore
from goods where state=#{state} and app_id=#{appId} 

	and class_id3=#{classId}


	and goods_name like concat("%",#{goodsName},"%")


	order by create_time desc



	order by price desc


	order by price asc



	order by count desc

limit #{pageNo},#{pageSize}

mapper.update:

update goods   set     
    state=#{state} 
where id in     

 #{item}

mapper.select:
select goods_name goodsName,id goodsId,banner_url bannerUrl,price,sku_store skuStore,
detail_url detailUrl,multi_index multiIndex ,store_id storeId,state
from goods where id=#{appId}

update.foreach:
update goods

    sku_store=sku_store-#{item.count}

where id=#{item.goodsId} and sku_store-#{item.count} >0

mapper.select:
SELECT w.id id, w.goods_name goodsName, w.price price, w.sku_store skuStore, w.banner_url bannerUrl, w.detail_url detailUrl, w.multi_index multiIndex, w.class_id1 classId1, w.class_id2 classId2, w.class_id3 classId3, w.state state, w.create_time createTime, s.store_name storeName FROM goods w INNER JOIN store s on w.store_id =s.id
w.app_id=#{appId}
ORDER BY w.create_time desc LIMIT #{offSet}, #{pageRow}

SELECT w.id id, w.goods_name goodsName, w.price price, w.sku_store skuStore, w.banner_url bannerUrl, w.detail_url detailUrl, w.multi_index multiIndex, w.class_id1 classId1, w.class_id2 classId2, w.class_id3 classId3, w.state state, w.create_time createTime, s.store_name storeName FROM goods w INNER JOIN store s on w.store_id =s.id where 1=1
and w.app_id=#{appId}
and w.class_id1=#{classId}
ORDER BY w.count desc LIMIT #{pageNo}, #{pageSize}
