mapper.select:
SELECT count(0)
FROM store w
WHERE w.app_id =#{appId}

SELECT w.id id, w.store_name storeName, w.state state, c.class_name className, c.id storeClassId, w.area_info areaInfo , w.area_id_one areaIdOne , w.area_id_two areaIdTwo , w.area_id_three areaIdThree, w.create_time createTime, w.img_url imgUrl, w.logo_url logoUrl, w.name,w.telephone FROM store w inner join store_class c on w.store_class_id=c.id WHERE w.app_id =#{appId}
and w.store_name like concat("%",#{storeName},"%")
and w.state=#{state}
and w.create_time between #{startTime} and #{endTime}
and c.id = #{storeClassId}
order by w.create_time desc LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE store
SET
   store_name= #{storeName},user_id=#{userId},store_class_id=#{storeClassId},
   area_info=#{areaInfo},area_id_one=#{areaIdOne},area_id_two=#{areaIdTwo},area_id_three=#{areaIdThree},
 img_url= #{imgUrl},logo_url=#{logoUrl},name=#{name},telephone=#{telephone}
WHERE id = #{id}
