mapper.select:
SELECT w.id id, w.class_name className, w.class_img_url classImgUrl, w.class_level classLevel, w.parent_id parentId, w.show_order showOrder FROM goods_class w
parent_id=#{parentId}
class_level=1
ORDER BY w.show_order LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE goods_class
SET
    class_name = #{className},class_img_url=#{classImgUrl},show_order=#{showOrder}
WHERE id = #{id}
